export const getTranslation = (key: string, language: string = 'en'): string | any => {
  const translation = translations[key as keyof typeof translations];
  if (!translation) return key;
  
  // Handle array types like practiceScenarios
  if (Array.isArray(translation[language as keyof typeof translation])) {
    return translation[language as keyof typeof translation] || translation.en || [];
  }
  
  return translation[language as keyof typeof translation] || translation.en || key;
};